        "$:/plugins/danielo515/tiddlypouch/ui/revisions": {
            "title": "$:/plugins/danielo515/tiddlypouch/ui/revisions",
            "caption": "Revisions",
            "tags": "$:/tags/TiddlerInfo",
            "text": "<$button message="tm-tp-load-revisions" param={{!!title}}>
    Reload revisions
</$button>
<table>
<$list filter="[{!!title}addprefix[$:/temp/revisions:]]" variable="rev-store">
    <$list filter="[<rev-store>indexes[]nsort[]]" variable="index">
        <$list filter="[<rev-store>getindex<index>]" variable="revision">
            <tr><td><$button class="tc-btn-invisible tc-tiddlylink">
                <$action-sendmessage $message="tm-tp-load-certain-revision" $param={{!!title}} revision=<<revision>>/>
                <$text text=<<revision>>/>
            </$button>
            </td></tr>
        </$list>
    </$list>
</$list>
</table>"
        },
        "$:/plugins/danielo515/tiddlypouch/ui/revisions/side-view": {
            "created": "20170611142115719",
            "creator": "danielo515",
            "modified": "20170611153258197",
            "modifier": "danielo515",
            "tags": "",
            "title": "$:/plugins/danielo515/tiddlypouch/ui/revisions/side-view",
            "type": "text/vnd.tiddlywiki",
            "text": "<!-- This is just a template. It should be used to display different revisions of the same tiddler left and right -->
<$transclude tiddler="$:/core/ui/TiddlerFields"/>

<$list filter="[<currentTiddler>has[text]]">
<$codeblock code={{!!text}} language={{!!type}}/>
</$list>
<$list filter="[<currentTiddler>!has[text]]">
@@color:red;
There is nothing to compare with
@@
</$list>"
        },
        "$:/plugins/danielo515/tiddlypouch/macros/tabs-state": {
            "title": "$:/plugins/danielo515/tiddlypouch/macros/tabs-state",
            "tags": "$:/tags/Macro",
            "text": "\define tabs-state(tabsList,default,state:"$:/state/tab",class,template)
<div class="tc-tab-set $class$">
<div class="tc-tab-buttons $class$">
<$list filter="$tabsList$" variable="currentTab">
<$button set=<<tiddlerInfoTabState>> setTo=<<currentTab>> default="$default$" selectedClass="tc-tab-selected">
<$transclude tiddler=<<currentTab>> field="caption">
<$macrocall $name="currentTab" $type="text/plain" $output="text/plain"/>
</$transclude>
</$button>
</$list>
</div><div class="tc-tab-divider $class$"/><div class="tc-tab-content $class$">
<$list filter="$tabsList$" variable="currentTab">

<$reveal type="match" state=<<tiddlerInfoTabState>> text=<<currentTab>> default="$default$">

<$transclude tiddler="$template$" mode="block">

<$transclude tiddler=<<currentTab>> mode="block"/>

</$transclude>

</$reveal>

</$list>
</div>
</div>
\end"
        },
        "$:/plugins/danielo515/tiddlypouch/macros/navigate-to-controlPanel-tab": {
            "title": "$:/plugins/danielo515/tiddlypouch/macros/navigate-to-controlPanel-tab",
            "tags": "$:/tags/Macro",
            "text": "\define control-panel-tab(caption target)
<$button class="tc-btn-invisible tc-tiddlylink">
    <$action-setfield $tiddler="$:/state/tab-1749438307" text="$target$" />
    <$action-navigate $to="$:/ControlPanel" />
    $caption$
</$button>
\end

\define tiddlypouch-tab(caption target)
<$button class="tc-btn-invisible tc-tiddlylink">
    <$action-setfield $tiddler="$:/state/tab-1749438307" text="$:/plugins/danielo515/tiddlypouch/ControlPanel" />
    <$action-setfield $tiddler="$:/state/TiddlyPouch/tabs--1566775042" text="$:/plugins/danielo515/tiddlypouch/ControlPanel/$target$"/>
    <$action-navigate $to="$:/ControlPanel" />
    <$text text="$caption$"/>
</$button>
\end"
        },
